import axios from "axios";
import { showNotification } from "../slice/uiSlice";
import { replaceCart } from "../slice/cartSlice";


export const fetchCartData = () => {
  return async (dispatch) => {
    const fetchData = async () => {
      const res = await axios.get(
        `https://redux-http-97631-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json`
      );

      // if (!response.ok) {
      //   throw new Error("Could not fetch cart data!");
      // }

      const data = await res.json();
      return data;
    };

    try {
      const cartData = await fetchData();
      dispatch(replaceCart(cartData));
    } catch (error) {
      dispatch(
        showNotification({
          status: "error",
          title: "Error!",
          message: "Fetching cart data failed!",
        })
      );
    }
  };
};

export const getCartData = () => {
  return async (dispatch) => {
    try {
      const res = await axios.get(`https://redux-http-97631-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json`)
      dispatch(replaceCart(res))
    } catch (err) {
      dispatch(showNotification({
        status: 'error',
        title: 'Error!',
        message: 'Fetching cart data failed!'
      }));
    }
  }
}

export const postCartData = (cart) => {
  return async (dispatch) => {
    dispatch(showNotification({
      status: 'pending',
      title: 'Sending...',
      message: 'Sending cart data!'
    }));
    try {
      await axios.put(`https://redux-http-97631-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json`, cart)
      dispatch(showNotification({
        status: 'success',
        title: 'Success!',
        message: 'Sending cart data successfully!'
      }));
    } catch (err) {
      dispatch(showNotification({
        status: 'error',
        title: 'Error!',
        message: 'Sent cart data failed!'
      }));
    }
  }
}
