import classes from './CartItem.module.css';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import { addItemToCart, removeItemFromCart } from '../../redux/slice/cartSlice';

const CartItemContainer = styled.li`
  margin: 1rem 0;
  background-color: #575757;
  padding: 1rem;

  > header {
    display: flex;
  justify-content: space-between;
  align-items: baseline;
  }
`;

function CartItem({ list }) {
  const dispatch = useDispatch();

  const removeItemHandler = () => {
    dispatch(removeItemFromCart(list.id));
  }

  const addItemHandler = () => {
    dispatch(addItemToCart({
      id: list.id,
      title: list.title,
      price: list.price
    }))
  }

  return (
    <CartItemContainer>
      <header>
        <h3>{list.title}</h3>
        <div className={classes.price}>
          ${list.totalPrice.toFixed(2)}<br />
          <span className={classes.itemprice}>(${list.price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{list.count}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={removeItemHandler}>-</button>
          <button onClick={addItemHandler}>+</button>
        </div>
      </div>
    </CartItemContainer>
  );
};

export default CartItem;
