import { createSlice } from "@reduxjs/toolkit";
import { showNotification } from "./uiSlice";
import axios from "axios";

const initialState = {
  items: [],
  totalCount: 0,
  totalPrice: 0
};

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((value) => value.id === newItem.id);
      state.totalCount++;
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          title: newItem.title,
          price: newItem.price,
          count: 1,
          totalPrice: newItem.price
        });
      } else {
        existingItem.count++;
        existingItem.totalPrice += newItem.price;
      }
    },
    removeItemFromCart(state, action) {
      const removeItemId = action.payload;
      const existingItem = state.items.find((value) => value.id === removeItemId);
      state.totalCount--;
      if (existingItem.count === 1) {
        state.items = state.items.filter((value) => value.id !== removeItemId);
      } else {
        existingItem.count--;
        existingItem.totalPrice -= existingItem.price;
      }
    }
  }
})

const sendCartData = (cartData) => {
  return async (dispatch) => {
    dispatch(showNotification({
      status: 'pending',
      title: 'Sending...',
      message: 'Sending cart data!'
    }));
    try {
      await axios.put(`https://redux-http-97631-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json`, cart)
      dispatch(showNotification({
        status: 'success',
        title: 'Success!',
        message: 'Sending cart data successfully!'
      }));
    } catch (err) {
      dispatch(showNotification({
        status: 'error',
        title: 'Error!',
        message: 'Sent cart data failed!'
      }));
    }
  }
}

export const { addItemToCart, removeItemFromCart } = cartSlice.actions;
export default cartSlice;
